import React, { useState } from "react";
import "./App.css";
import AssetTable from "./components/AssetTable";
import AssetAnalysis from "./components/AssetAnalysis";

function App() {
  const [tab, setTab] = useState("portfolio");
  return (
    <div className="app-container">
      <header className="header">
        <div className="header-row">
          <div className="logo-title">
            <h1>Port Penguin</h1>
          </div>
          <nav className="tabs" aria-label="Main navigation">
            <button className={tab === "portfolio" ? "tab-btn active" : "tab-btn"} onClick={() => setTab("portfolio")} aria-current={tab === "portfolio" ? "page" : undefined}>Portfolio</button>
            <button className={tab === "analysis" ? "tab-btn active" : "tab-btn"} onClick={() => setTab("analysis")} aria-current={tab === "analysis" ? "page" : undefined}>Asset Analysis</button>
          </nav>
          <div className="header-actions">
            <button className="sign-btn" aria-label="Sign in or sign up">Sign In / Sign Up</button>
          </div>
        </div>
        <p className="subtitle">Simple Portfolio Weighting Tool</p>
      </header>
      <main>
        {tab === "portfolio" ? <AssetTable /> : <AssetAnalysis />}
      </main>
      <footer className="footer">Â© 2025 Port Penguin</footer>
      <button className="feedback-btn" aria-label="Send feedback">
        <svg width="22" height="22" viewBox="0 0 22 22" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M2 11L20 3L12 21L10 13L2 11Z" fill="#171515" stroke="#FFB000" strokeWidth="2" strokeLinejoin="round"/>
        </svg>
      </button>
    </div>
  );
}

export default App;
