import React, { useState } from "react";
import "./App.css";
import AssetTable from "./components/AssetTable";
import AssetAnalysis from "./components/AssetAnalysis";

function App() {
  const [tab, setTab] = useState("portfolio");
  return (
    <div className="app-container">
      <header className="header">
        <h1>Port Penguin</h1>
        <button className="sign-btn">Sign In / Sign Up</button>
        <p className="subtitle">Simple Portfolio Weighting Tool</p>
        <div className="tabs">
          <button className={tab === "portfolio" ? "tab-btn active" : "tab-btn"} onClick={() => setTab("portfolio")}>Portfolio</button>
          <button className={tab === "analysis" ? "tab-btn active" : "tab-btn"} onClick={() => setTab("analysis")}>Asset Analysis</button>
        </div>
      </header>
      <main>
        {tab === "portfolio" ? <AssetTable /> : <AssetAnalysis />}
      </main>
      <footer className="footer">Â© 2025 Port Penguin</footer>
      <button className="feedback-btn">Feedback</button>
    </div>
  );
}

export default App;
